#создаем список ключей
def create_key_list(text_list:dict) -> dict:
    key_list = []
    for i in range(0, len(text_list), 5):
        key_list.append(text_list[i])
    # for i in range(0, len(key_list)):
    #     if text_list.count(key_list[i]):
    #         text_list.remove(key_list[i])
    return key_list

#создаем список значений
def create_value_list(text_list:dict) -> dict:
    value_list = []
    for i in range(0, len(text_list), 5):
        value_list.append(text_list[i + 1: i + 5])
    return value_list

